import React, { useEffect, useState } from 'react'
import { Link, useParams } from 'react-router-dom'
import ContactServices from '../../ContactServices/ContactServices';

const ViewContact = () => {
  let {contactId}=useParams();
  let[state, setState]=useState({
    loading:false,
    contacts:[],
    errorMessage:""
  })

  useEffect(()=>{
    let promise=new Promise((res, rej)=>{
      setState({...state, loading:true})
      let response=ContactServices.getContact(contactId);
      res(response)
      rej("error")
    })
    promise.then((res1)=>{
      setState({...state,loading:false, contacts:res1.data})
    }).catch(()=>{
      setState({...state,loading:false, errorMessage:"data is not av"})
    })
  },[contactId])
  return (
    <div>
      <h2>{contactId}</h2>
      <div className="view-contact my-3">
        <div className="container align-items-center">
          <div className="row">
            <div className="col-md-4 my-3">
              <img src="https://static.vecteezy.com/system/resources/previews/009/397/892/non_2x/woman-face-expression-clipart-design-illustration-free-png.png" alt="" className='img-fluid contact-img' />
            </div>
          </div>

          <div className="row">
            <div className="col-md-6">
              <ul className="list-group">
                <li className="list-group-item list-group-item-action">Name: <span className='fw-bold ms-1'>Arti</span></li>
                <li className="list-group-item list-group-item-action">Email: <span className='fw-bold ms-1'>arti@gmail.com</span></li>
                <li className="list-group-item list-group-item-action">Contact: <span className='fw-bold ms-1'>8975666077</span></li>
                <li className="list-group-item list-group-item-action">Company: <span className='fw-bold ms-1'>TCS</span></li>
                <li className="list-group-item list-group-item-action">Title: <span className='fw-bold ms-1'>Java Developer</span></li>
                <li className="list-group-item list-group-item-action">Group: <span className='fw-bold ms-1'>1</span></li>
              </ul>
            </div>
          </div>

          <div className="row">
            <div className="col-md-2">
              <Link to={'/'} className='btn btn-warning my-2 '> Back</Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default ViewContact
