import React, { useEffect, useState } from 'react'
import WeatherService from './WeatherService'

const View = () => {
    let [state, setState] = useState({
        loading: false,
        temperature: [],
        errorMessage: ""
    })
    useEffect(()=>{
        let promise=new Promise((res, rej)=>{
            setState({...state, loading:true})
            let response=WeatherService.getData()
            res(response)
        })
        promise.then((re1)=>{
            setState({...state, loading:false, temperature:re1.data})
        }).catch(()=>{
            setState({...state, loading:false, errorMessage:alert("data is not available")})
        })
    },[])
    return (
        <div>
            <div className="container">
                <div className="row">
                    <div className="col-md-6">
                        <div className="card">
                            <div className="card-title"></div>
                            <div className="card-body">
                                <div className="row">
                                    <div className="col-md-6">
                                        <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQlJDp_1SomEP63vWTV0G5q4HJUc1eZq5fwuiIwcaGOtQ&s" alt="" />
                                    </div>
                                    <div className="col-md-6">
                                        <ul className="list-group">
                                            <li className="list-group-item list-group-item-action">temperature</li>
                                            <li className="list-group-item list-group-item-action">hum</li>
                                            <li className="list-group-item list-group-item-action">in</li>
                                        </ul>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default View
